/**
 * rhd_site_navigation function.
 *
 * @access public
 * @return void
 */
function rhd_site_navigation() {

	echo "<a href='" . home_url( '/#top' ) . "'>\n";
	rhd_svg_logo_nav();
	echo "</a>";

	$nav_args_main = array(
		'theme_location' => 'primary',
		'menu_id' => 'site-navigation',
		'menu_class' => 'site-navigation',
		'container' => 'nav',
		'container_id' => 'site-navigation-container',
		'walker' => new RHD_Walker_Nav
	);
	wp_nav_menu( $nav_args_main );

	echo '<button id="hamburger" class="sb-toggle-right c-hamburger c-hamburger--rot"><span>Toggle nav</span></button>';
}
